CREATE TABLE IF NOT EXISTS `【tableName】` (    id          INTEGER  PRIMARY KEY ASC AUTOINCREMENT NOT NULL,    [key]       STRING,    value       STRING,    code        STRING,    remark      STRING,    parent      STRING,    n_number    DECIMAL,    n_score     DECIMAL,    create_time DATETIME NOT NULL DEFAULT (datetime('now', 'localtime')),    create_by   STRING,    modify_time DATETIME NOT NULL DEFAULT (datetime('now', 'localtime')),    modify_by   STRING,    c_mode      STRING,    c_order     INTEGER NOT NULL DEFAULT 1,    c_group     STRING,    c_children_info  STRING,    c_expand_info    STRING,    c_version   STRING NOT NULL DEFAULT 1,    c_status    TINYINT NOT NULL DEFAULT 0,    is_delete   TINYINT NOT NULL DEFAULT [0] CHECK (is_delete < 2),    is_display  TINYINT NOT NULL DEFAULT 1 CHECK (is_display < 2),    is_expand  TINYINT NOT NULL DEFAULT 0 CHECK (is_expand < 2),    expand1     STRING,    expand2     STRING,    expand3     STRING,    expand4     STRING,    expand5     STRING);CREATE UNIQUE INDEX "idx_code" ON `【tableName】` ( "code" );CREATE INDEX "idx_key" ON `【tableName】` ( "key" );